mapper.select:
SELECT MEMBER_NO, GRADE_NO, TEAM_NO, MEMBER_NAME FROM "MEMBER" WHERE TEAM_NO=0
UNION
SELECT MEMBER_NO, GRADE_NO, TEAM_NO, MEMBER_NAME FROM "MEMBER" WHERE DEPARTMENT_NO = (SELECT DEPARTMENT_NO FROM "MEMBER" WHERE MEMBER_NO=#{memberNo}) AND GRADE_NO=2 AND MEMBER_NO!=#{memberNo}
UNION
SELECT MEMBER_NO, GRADE_NO, TEAM_NO, MEMBER_NAME FROM "MEMBER" WHERE TEAM_NO = (SELECT TEAM_NO FROM "MEMBER" WHERE MEMBER_NO=#{memberNo}) AND GRADE_NO=3 AND MEMBER_NO!=#{memberNo}
