mapper.select:
SELECT COUNT(*) FROM "BOARD"
WHERE BOARD_DEL_FL = 'N'
AND BOARD_CODE_NO = #{boardCodeNo}

SELECT COUNT(*) FROM "BOOKMARK"
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM "BOOKMARK"
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE "BOARD" SET
BOARD_HITS = BOARD_HITS + 1
WHERE BOARD_NO = #{boardNo}
