mapper.delete:
UPDATE "BOARD" SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_CODE_NO = #{boardCodeNo}
AND BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE "BOARD" SET
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent}
WHERE BOARD_CODE_NO = #{boardCodeNo}
AND BOARD_NO = #{boardNo}
