mapper.select:
SELECT GOODS_NO, GOODS_NAME, GOODS_STANDARD, LCATEGORY_NAME, SCATEGORY_NAME, STORE_NAME, STOCK_IN_PRICE, STOCK_OUT_PRICE, STOCK_DISCOUNT, STOCK_AMOUNT, STORE_NO FROM GOODS JOIN LCATEGORY USING(LCATEGORY_NO) JOIN SCATEGORY USING(SCATEGORY_NO) JOIN STOCK USING(GOODS_NO) JOIN STORE USING(STORE_NO) WHERE STORE_NO =
0
#{storeNo}
AND LCATEGORY_NAME = #{lcategoryName}
AND SCATEGORY_NAME = #{scategoryName}
AND GOODS_NAME LIKE '%${goodsName}%' ORDER BY GOODS_NO DESC

SELECT STORE_NAME
FROM STORE
WHERE STORE_NO = #{storeNo}

SELECT LCATEGORY_NAME, SCATEGORY_NAME, GOODS_NAME, GOODS_NO, GOODS_STANDARD FROM GOODS JOIN LCATEGORY USING(LCATEGORY_NO) JOIN SCATEGORY USING(SCATEGORY_NO) WHERE GOODS_NAME LIKE '%${goodsName}%'
AND LCATEGORY_NAME = #{lcategoryName}
AND SCATEGORY_NAME = #{scategoryName}
ORDER BY GOODS_NO DESC

mapper.update:
UPDATE "STOCK" SET
STOCK_IN_PRICE = #{stockInPrice}
WHERE STORE_NO = #{storeNo}
AND GOODS_NO = #{goodsNo}

mapper.select:
SELECT LCATEGORY_NAME, SCATEGORY_NAME, GOODS_NAME, GOODS_NO, GOODS_STANDARD, STORE_NO FROM GOODS JOIN LCATEGORY USING(LCATEGORY_NO) JOIN SCATEGORY USING(SCATEGORY_NO) JOIN STOCK USING(GOODS_NO) WHERE STORE_NO = 0
AND GOODS_NAME LIKE '%${goodsName}%'
AND LCATEGORY_NAME = #{lcategoryName}
AND SCATEGORY_NAME = #{scategoryName}
ORDER BY GOODS_NO DESC
